import React from "react";
import { NavLink, useParams } from "react-router-dom";
import { Button, Card, CardBody, CardGroup, CardImg, CardTitle } from "reactstrap";

const GameProtectedIndex = ({ currentUser, games }) => {
    const { id } = useParams();
    const userGames = games?.filter((game) => game.userId === currentUser?.id && game.id === +id);

    return (
      <>
        <h1>My Games</h1>
        {currentUser && (
          <CardGroup>
            {userGames?.map((game, index) => (
              <Card key={index} className="mb-3" style={{ width: "18rem" }}>
                <CardImg alt="game" src={game.image} top width="100%" />
                <CardBody>
                  <CardTitle tag="h5">{game.title}</CardTitle>
                  <Button>
                    <NavLink to={`/GameShow/${game.id}`}>Show Game</NavLink>
                  </Button>
                </CardBody>
              </Card>
            ))}
          </CardGroup>
        )}
      </>
    );
  };

  export default GameProtectedIndex;
