import React, { useState, useEffect } from 'react';

const SearchComponent = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    // Fetch search results when searchQuery changes
    if (searchQuery) {
      fetchSearchResults();
    }
  }, [searchQuery]);

  const fetchSearchResults = async () => {
    try {
      const apiKey = '5d37a51f335c471490ba1b0276d510f8'; // Replace with your actual API key
      const response = await fetch(`https://api.rawg.io/api/games?key=${apiKey}&search=${searchQuery}`);
      const data = await response.json();
      const gamesArray = data.results;
      setSearchResults(gamesArray);
    } catch (error) {
      console.error('Error fetching search results:', error);
    }
  };

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  const handleSaveGame = (game) => {
    // Replace with your save game logic
    console.log('Saving game:', game);
  };

  return (
    <div className="search-container">
    <input
      type="text"
      value={searchQuery}
      onChange={handleSearchChange}
      placeholder="Search games"
      className="search-input" // Add a CSS class for the search input
    />
    <ul className="search-results"> 
      {searchResults.map((game) => (
        <li key={game.id} className="search-result"> // Add a CSS class for each search result item
          <div>Title: {game.name}</div>
          <div>Genre: {game.genres[0]?.name}</div>
          <div>Platform: {game.platforms[0]?.platform.name}</div>
          <div>Image: <img src={game.background_image} alt={game.name} /></div>
          <button onClick={() => handleSaveGame(game)} className="save-button">Save</button> // Add a CSS class for the save button
        </li>
      ))}
    </ul>
  </div>
  );
};

export default SearchComponent;
