import React, { useEffect, useState } from "react";
import gamingJess from "../assets/gaming-jess.png";
import gamingVance from "../assets/gaming-vance.png";
import pipeImage from "../assets/pipe.png";

const WhacADev = () => {
  const [currMoleTile, setCurrMoleTile] = useState(null);
  const [currPlantTile, setCurrPlantTile] = useState(null);
  const [score, setScore] = useState(0);
  const [gameOver, setGameOver] = useState(false);

  useEffect(() => {
    setGame();
  }, []);

  function setGame() {
    setInterval(setMole, 1000);
    setInterval(setPlant, 2000);
  }

  function getRandomTile() {
    let num = Math.floor(Math.random() * 9);
    return num.toString();
  }

  function setMole() {
    if (gameOver) {
      return;
    }
    if (currMoleTile) {
      setCurrMoleTile(null);
    }

    let num = getRandomTile();
    if (currPlantTile && currPlantTile.id === num) {
      return;
    }

    setCurrMoleTile(num);
  }

  function setPlant() {
    if (gameOver) {
      return;
    }
    if (currPlantTile) {
      setCurrPlantTile(null);
    }

    let num = getRandomTile();
    if (currMoleTile && currMoleTile === num) {
      return;
    }

    setCurrPlantTile(num);
  }

  function selectTile(event) {
    if (gameOver) {
      return;
    }
    const selectedTileId = event.target.id;
    if (selectedTileId === currMoleTile) {
      setScore((prevScore) => prevScore + 10);
    } else if (selectedTileId === currPlantTile) {
      setScore("GAME OVER: " + score.toString());
      setGameOver(true);
    }
  }

  return (
    <div>
      {/* Render the game board and score */}
      <div
        id="board"
        style={{
          width: "640px",
          height: "640px",
          margin: "0 auto",
          display: "flex",
          flexWrap: "wrap",
          border: "3px solid white",
          borderRadius: "25px",
        //   background: `url(${pipeImage})`,
        }}
      >
        {/* Render the grid tiles */}
        {[...Array(9)].map((_, index) => (
          <div
            className="tile"
            key={index}
            id={index.toString()}
            onClick={selectTile}
            style={{
              width: "180px",
              height: "180px",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              position: "relative",
            }}
          >
            {index.toString() === currMoleTile && (
              <img
                src={gamingJess}
                className="img"
                alt="mole"
                style={{
                  width: "100px",
                  height: "100px",
                  userSelect: "none",
                  MozUserSelect: "none",
                  WebkitUserDrag: "none",
                  WebkitUserSelect: "none",
                  msUserSelect: "none",
                  position: "absolute",
                  top: "50%",
                  left: "50%",
                  transform: "translate(-50%, -50%)",
                }}
              />
            )}
            {index.toString() === currPlantTile && (
              <img
                src={gamingVance}
                className="img"
                alt="plant"
                style={{
                  width: "100px",
                  height: "100px",
                  userSelect: "none",
                  MozUserSelect: "none",
                  WebkitUserDrag: "none",
                  WebkitUserSelect: "none",
                  msUserSelect: "none",
                  position: "absolute",
                  top: "50%",
                  left: "50%",
                  transform: "translate(-50%, -50%)",
                }}
              />
            )}
          </div>
        ))}
      </div>
      <div id="score">{gameOver ? `GAME OVER: ${score}` : score}</div>
    </div>
  );
};

export default WhacADev;
