const SearchComponent = ({ searchTerm, handleSearch }) => {
    const [searchQuery, setSearchQuery] = useState('');
    const [searchResults, setSearchResults] = useState([]);

    useEffect(() => {
      // Fetch search results when searchQuery changes
      if (searchQuery) {
        fetchSearchResults();
      }
    }, [searchQuery]);

    const fetchSearchResults = async () => {
      try {
        const apiKey = '5d37a51f335c471490ba1b0276d510f8'; // Replace with your actual API key
        const response = await fetch(`https://api.rawg.io/api/games?key=${apiKey}&search=${searchQuery}`);
        const data = await response.json();
        const gamesArray = data.results;
        setSearchResults(gamesArray);
      } catch (error) {
        console.error('Error fetching search results:', error);
      }
    };

    const handleSearchChange = (event) => {
      setSearchQuery(event.target.value);
    };

    const handleSaveGame = (game) => {
      // Replace with your save game logic
      console.log('Saving game:', game);
    };

    return (
      <div className="search-container">
        <input
          type="text"
          value={searchTerm}
          onChange={handleSearch}
          placeholder="Search games"
          className="search-input"
        />
        <ul className="search-results">
          {searchResults.map((game) => (
            <li key={game.id} className="search-result">
              <div className="game-title">Title: {game.name}</div>
              <div className="game-info">
                <div className="game-info-item">Genre: {game.genres[0]?.name}</div>
                <div className="game-info-item">Platform: {game.platforms[0]?.platform.name}</div>
              </div>
              <div className="game-image">
                <img src={game.background_image} alt={game.name} />
              </div>
              <button onClick={() => handleSaveGame(game)} className="save-button">
                Save
              </button>
            </li>
          ))}
        </ul>
      </div>
    );
  };

export default SearchComponent;
