import React, { useState, useEffect } from "react";
import { FormGroup, Label, Input, Button } from "reactstrap";
import { useNavigate, useLocation } from "react-router-dom";

const GameNew = ({ createGame, currentUser }) => {
  const navigate = useNavigate();
  const location = useLocation();

  const [newGame, setNewGame] = useState({
    title: "",
    image: "",
    genre: "",
    platform: "",
    notes: "",
    user_id: currentUser?.id || "",
  });

  // Use of useEffect to prefill the form fields when location state is available
  useEffect(() => {
    if (location.state && location.state.prefill) {
      const { title, image, genre, platform } = location.state.prefill;
      setNewGame((prevGame) => ({
        ...prevGame,
        title: title || "",
        image: image || "",
        genre: genre || "",
        platform: platform || "",
      }));
    }
  }, [location.state]);

  const handleChange = (e) => {
    setNewGame((prevGame) => ({
      ...prevGame,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const gameData = { ...newGame, user_id: currentUser?.id || "" };
    createGame(gameData);
    navigate("/mygames");
  };

  // Image preview handler
  const handleImagePreview = (e) => {
    const imageFile = e.target.files[0];
    const imageUrl = URL.createObjectURL(imageFile);
    setNewGame((prevGame) => ({
      ...prevGame,
      image: imageUrl,
    }));
  };

  return (
    <>
      <div className="add-a-game-title">
        <img src={require("../assets/addagame.png")} alt="bubble" />
      </div>
      {newGame.image && (
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            marginTop: "20px",
          }}
        >
          <img
            src={newGame.image}
            alt={newGame.image}
            height="200px"
            width="200px"
          />
        </div>
      )}

      <form className="new-game-form" onSubmit={handleSubmit}>
        <div className="form-group">
          <FormGroup>
            <Input
              id="game-title"
              type="text"
              name="title"
              placeholder="Game Title"
              onChange={handleChange}
              value={newGame.title}
            />
          </FormGroup>

          <FormGroup>
            <Input
              id="game-image"
              type="file"
              accept="image/*"
              name="image"
              placeholder="Image URL"
              onChange={handleImagePreview}
            />
          </FormGroup>

          {/* Remaining form fields */}

          <Button type="submit" className="pixel-btn">
            Add Game
          </Button>
        </div>
      </form>
    </>
  );
};

export default GameNew;
