import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";

const Header = ({ currentUser, logout }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const navigate = useNavigate();

  const handleClick = () => {
    logout();
    navigate("/");
  };

  const toggleMenu = (event) => {
    event.stopPropagation();
    setIsMenuOpen((prevState) => !prevState);
  };
  useEffect(() => {
    const closeMenu = () => {
      setIsMenuOpen(false);
    };

    if (isMenuOpen) {
      document.addEventListener("click", closeMenu);
    }

    return () => {
      document.removeEventListener("click", closeMenu);
    };
  }, [isMenuOpen]);

  const renderAuthButtons = () => {
    if (currentUser) {
      return (
        <>
        <ul class="flex border-b">
        <li class="-mb-px mr-1">
        <a class="bg-white inline-block border-l border-t border-r rounded-t py-2 px-4 text-blue-700 font-semibold" href="#">My Games</a>
          </li>
          <li class="-mb-px mr-1">
          <Link to="/gamenew">Add Game</Link>
          </li>
          <li class="-mb-px mr-1">
          <Link to="/">
            {/* <button className="pixel-btn" role="button" onClick={handleClick}> */}
            Log Off
          </Link>
          </li>
          </ul>
        </>
      );
    } else {
      return (
        <>
          <Link to="/signup">Sign Up</Link>
          <Link to="/login">Login</Link>
        </>
      );
    }
  };

  return (
    <>
      <nav id="nav">
        <button
          className="burger-menu"
          onClick={toggleMenu}
          aria-label="Toggle menu"
        >
          â˜°
        </button>
        <div className={`menu ${isMenuOpen ? "open" : ""}`}>
          <Link to="/">Home</Link>
          <Link to="/games">Browse</Link>
          {renderAuthButtons()}
        </div>
      </nav>
    </>
  );
};

export default Header;
