import React, { useState, useEffect } from "react";
import { FormGroup, Label, Input, Button } from "reactstrap";
import { useNavigate, useLocation } from "react-router-dom";

const GameNew = ({ createGame, currentUser }) => {
  const navigate = useNavigate();
  const location = useLocation();

  const [newGame, setNewGame] = useState({
    title: "",
    image: "",
    genre: "",
    platform: "",
    notes: "",
    user_id: currentUser?.id || "",
  });

  // Use of useEffect to prefill the form fields when location state is available
  useEffect(() => {
    if (location.state && location.state.prefill) {
      const { title, image, genre, platform } = location.state.prefill;
      setNewGame((prevGame) => ({
        ...prevGame,
        title: title || "",
        image: image || "",
        genre: genre || "",
        platform: platform || "",
      }));
    }
  }, [location.state]);

  // Check if prefill exists before accessing its properties
  const prefillImage = location.state && location.state.prefill ? location.state.prefill.image : '';

  const handleChange = (e) => {
    setNewGame((prevGame) => ({
      ...prevGame,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const gameData = { ...newGame, user_id: currentUser?.id || "" };
    createGame(gameData);
    navigate("/mygames");
  };

  return (
    <>
      <div className="add-a-game-title">
        <img src={require("../assets/addagame.png")} alt="bubble" />
      </div>
      {prefillImage && (
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            marginTop: "20px", // Adjust the margin top as needed
          }}
        >
          <div
            style={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
              height: "20%",
              width: "20%",
            }}
          >
            <img
              src={prefillImage}
              alt={prefillImage}
              height="100%"
              width="100%"
            />
          </div>
        </div>
      )}

      <form className="new-game-form" onSubmit={handleSubmit}>
        {/* Form fields */}
      </form>
    </>
  );
};

export default GameNew;
