import React from "react";
import { useParams } from "react-router-dom";
import {
  Card,
  CardBody,
  CardTitle,
  CardSubtitle,
  CardText,
  CardImg,
  Button,
} from "reactstrap";
import GameEdit from "./GameEdit";

const GameShow = ({ games, currentUser, deleteGame }) => {
  const { id } = useParams();
  const currentGame = games?.find((game) => game.id === +id);
  const [editMode, setEditMode] = useState(false);
  const handleDelete = () => {
    deleteGame(currentGame.id);
  };

  const renderEditDeleteButtons = () => {
    if (currentUser && currentGame && currentUser.id === currentGame.userId) {
      return (
        <>
          <Button>Edit Game</Button>
          <Button onClick={handleDelete}>Delete Game</Button>
        </>
      );
    }
    return null;
  };

  return (
    <main>
      {currentGame && (
        <div className="show-container">
          <Card className="show-card">
            <CardImg className="show-img" alt="" src={currentGame.image} />
            <CardBody className="show-body">
              <CardTitle
                style={{
                  fontWeight: "800",
                  fontSize: "3rem",
                  marginBlock: "2rem",
                }}
              >
                {currentGame.title}
              </CardTitle>
              <CardSubtitle
                className="card-title"
                style={{
                  fontWeight: "500",
                  fontSize: "1.5rem",
                  marginBlock: "2rem",
                }}
              >
                Game Genre: {currentGame.genre} <br /> Game Platform:{" "}
                {currentGame.platform}
              </CardSubtitle>
              <CardText className="card-title">{currentGame.notes}</CardText>
              {renderEditDeleteButtons()} {/* Invoke the function */}
            </CardBody>
          </Card>
        </div>
      )}
    </main>
  );
};

export default GameShow;
