import React, { useState, useEffect } from "react";
import { Routes, Route } from "react-router-dom";
import Footer from "./components/Footer";
import Header from "./components/Header";
import GameEdit from "./pages/GameEdit";
import GameIndex from "./pages/GameIndex";
import GameNew from "./pages/GameNew";
import GameShow from "./pages/GameShow";
import Home from "./pages/Home";
import NotFound from "./pages/NotFound";
import mockGames from "./mockGames";
import mockUsers from "./mockUsers";
import Search from "./components/Search";
import LogIn from "./pages/LogIn";
import SignUp from "./pages/SignUp";
import AboutUs from "./pages/AboutUs";
import "./App.css";

const App = () => {
  const [games, setGames] = useState(mockGames);
  const [currentUser, setCurrentUser] = useState(mockUsers);

  const url = "http://localhost:3000"

  useEffect(() => {
    readGames()
  }, [])

  const readGames = () => {
    fetch(`${url}/games`)
     .then(response => response.json())
     .then(payload => {
       setGames(payload)
     })
     .catch((error) => console.log(error))
  }

  const createGame = (newGame) => {
    const updatedGames = [...games, newGame];
    setGames(updatedGames);
  };

  const updateGame = (updatedGame) => {
    const updatedGames = games.map((game) =>
      game.id === updatedGame.id ? updatedGame : game
    );
    setGames(updatedGames);
  };

  const login = (userInfo) => {
    fetch(`${url}/login`, {
      body: JSON.stringify(userInfo),
      headers: {
        "Content-Type": 'application/json',
        "Accept": 'application/json'
      },
      method: 'POST'
    })
      .then(response => {
       if(!response.ok) {
        throw Error(response.statusText)
       }
       localStorage.setItem("token", response.headers.get("Authorization"))
       return response.json()
      })
      .then(payload => {
        setCurrentUser(payload)
      })
      .catch(error => console.log("login errors:", error))
  }


  useEffect(() => {
    const loggedInUser = localStorage.getItem("token")
    if(loggedInUser) {
      setCurrentUser(loggedInUser)
    }
    readGames()
  }, [])

  return (
    <>
      <Header />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route
          path="/gameindex"
          element={<GameIndex games={games} />}
        />
        <Route
          path="/gameshow/:id"
          element={<GameShow games={games} />}
        />
        <Route
          path="/gamenew"
          element={<GameNew createGame={createGame} />}
        />
        <Route
          path="/gameedit/:id"
          element={<GameEdit games={games} updateGame={updateGame} />}
        />
        <Route path="/signup" element={<SignUp />} />
        <Route path="/login" element={<LogIn />} />
        <Route path="/aboutus" element={<AboutUs />} />
        <Route path="*" element={<NotFound />} />
      </Routes>
      <Footer />
    </>
  );
};

export default App;
